const spawnLocation = [
	{ x: 100, y: 100, used: false },
	{ x: 200, y: 200, used: false }
];

function allocateSpawnsforSacs() {
	var random = Math.floor(Math.random() * spawnLocation.length);
	trace(random);
	while (spawnLocation[random].used === true) {
		random = Math.floor(Math.random() * spawnLocation.length);
		spawnLocation[random].used = true;
	}
	return spawnLocation[random];
}

function populateSacs() {
	for (var i = 0; i < 2; i++) {
		sac.x = allocateSpawnsforSacs().x;
		sac.y = allocateSpawnsforSacs().y;
		sacs[i] = sac;
	}
}

const waveTimer = {
	remaining: 3600, //60sec at 60fps
	tick: function(rate) {
		this.remaining = this.remaining - rate;
	}
};

const player = {
	x: 24,
	y: 16,
	dx: 0,
	dy: 0,
	facing: 'r',
	spr: 256,
	lives: 3,
	health: 100,
	attacking: false,
	speed: 1,
	halted: false,
	move: function() {
		if (!this.halted) {
			if (this.canMoveDir('u')) {
				if (btn(0)) {
					this.dy -= 0.5;
					this.facing = 'u';
				}
			}
			if (this.canMoveDir('d')) {
				if (btn(1)) {
					this.dy += 0.5;
					this.facing = 'd';
				}
			}
			if (this.canMoveDir('l')) {
				if (btn(2)) {
					this.dx -= 0.5;
					this.facing = 'l';
				}
			}
			if (this.canMoveDir('r')) {
				if (btn(3)) {
					this.dx += 0.5;
					this.facing = 'r';
				}
			}

			this.x += this.dx;
			this.y += this.dy;
		}
	},
	stop: function() {
		this.dx = 0;
		this.dy = 0;
	},
	canMoveDir(direction) {
		if (direction == 'l') {
			if (pix(this.x - 1, this.y) == 0 && pix(this.x - 1, this.y + 5) == 0)
				return true;
		}
		if (direction == 'r') {
			if (pix(this.x + 6, this.y) == 0 && pix(this.x + 6, this.y + 5) == 0)
				return true;
		}
		if (direction == 'u') {
			if (pix(this.x, this.y - 1) == 0 && pix(this.x + 5, this.y - 1) == 0)
				return true;
		}
		if (direction == 'd') {
			if (pix(this.x, this.y + 6) == 0 && pix(this.x + 5, this.y + 6) == 0)
				return true;
		}
	},
	attack() {
		if (player.facing == 'r') {
			axe.x = player.x + 4;
			axe.y = player.y - 1;
			axe.flip = 0;
			axe.rotation = [0, 1];
		}

		if (player.facing == 'l') {
			axe.x = player.x - 6;
			axe.y = player.y - 1;
			axe.flip = 1;
			axe.rotation = [0, 1];
		}

		if (player.facing == 'u') {
			axe.x = player.x - 1;
			axe.y = player.y - 6;
			axe.flip = 3;
			axe.rotation = [1, 2];
		}

		if (player.facing == 'd') {
			axe.x = player.x - 1;
			axe.y = player.y + 4;
			axe.flip = 4;
			axe.rotation = [1, 2];
		}

		if (btn(4) && axe.cooldown <= 0) {
			axe.cooldown = 30;
			spr(axe.spr, axe.x, axe.y, 0, 1, axe.flip, axe.rotation[0]);
		} else if (axe.cooldown > 15) {
			axe.cooldown--;
			spr(axe.spr, axe.x, axe.y, 0, 1, axe.flip, axe.rotation[0]);
		} else if (axe.cooldown > 0) {
			axe.cooldown--;
			spr(axe.spr, axe.x, axe.y, 0, 1, axe.flip, axe.rotation[1]);
		}
	}
};

const axe = {
	x: 0,
	y: 0,
	spr: [272],
	rotation: [0, 0],
	flip: 0,
	hitEnemy: function() {
		//do stuff
	},
	cooldown: 0
};

function drawTitleScreen() {
	cls();

	if (btn(4)) {
		gameState.gameStart = true;
	}

	var button = 'A';
	if (!gameState.gameStart) {
		button = 'A';
		gameState.gameStartTimer = 120;
	} else if (gameState.gameStartTimer > 90) {
		button = '3';
		gameState.gameStartTimer--;
	} else if (gameState.gameStartTimer > 60) {
		button = '2';
		gameState.gameStartTimer--;
	} else if (gameState.gameStartTimer > 30) {
		button = '1';
		gameState.gameStartTimer--;
	} else {
		gameState.title = false;
	}

	print('MINITAUR MANIA', 86, 60);
	print('press [' + button + '] to start', 76, 80);
}

function drawGameOverScreen() {
	cls();

	if (btn(4)) {
		gameState.gameStart = true;
	}

	var button = 'A';
	if (!gameState.gameStart) {
		button = 'A';
		gameState.gameStartTimer = 120;
	} else if (gameState.gameStartTimer > 90) {
		button = '3';
		gameState.gameStartTimer--;
	} else if (gameState.gameStartTimer > 60) {
		button = '2';
		gameState.gameStartTimer--;
	} else if (gameState.gameStartTimer > 30) {
		button = '1';
		gameState.gameStartTimer--;
	} else {
		gameState.gameOver = false;
	}

	print('YOU FAILED TO CLAIM THE THRONE', 42, 60);
	print('press [' + button + '] to try again', 71, 80);
}

const isFirstRun = true;

function TIC() {
	init();

	if (gameState.title) {
		drawTitleScreen();
		return;
	}
	if (gameState.gameOver) {
		drawGameOverScreen();
		return;
	}
	cls();
	//drawTestmap();
	//most params are default, just manually entered them cuz not sure what they all did tbh
	map(0, 0, 30, 17, 0, 0, -1, 1, null);
	updateSacs();
	updateTimer();
	drawUI();
	updatePlayer();
	drawPlayer();
}

function init() {
	if (isFirstRun) {
		populateSacs();
		isFirstRun = false;
	}
}

function updatePlayer() {
	player.stop();
	player.move();
	player.attack();
}

function updateSacs() {
	for (var i = 0; i < sacs.length; i++) {
		sacs[i].stop();
		sacs[i].move();
		spr(sacs[i].spr, sacs[i].x, sacs[i].y, 0);
	}
}

function drawPlayer() {
	spr(player.spr, player.x, player.y, 0);
}

function updateTimer() {
	waveTimer.tick(gameState.difficulty);
	if (waveTimer.remaining <= 0) {
		gameState.gameOver = true;
		gameState.gameStart = false;
		waveTimer.remaining = 3600;
	}
}

function drawUI() {
	print('WAVE:', 164, 0, 1);
	for (i = 0; i < waveTimer.remaining / 75; i++) {
		rect(192 + i, 0, 1, 5, 1);
	}
}

